export class HomeComponent extends Destroy$ {
  constructor(cd, store, info, popup, swipeupService, slideService, filters) {
    super();
    this.cd = cd;
    this.store = store;
    this.info = info;
    this.popup = popup;
    this.swipeupService = swipeupService;
    this.slideService = slideService;
    this.filters = filters; //split the set all of posts into these (what we need)

    this.symbols = {
      userDraft: 0,
      userOnlinePost: 1,
      otherOnlinePost: 2,
      discard: -1
    };
    this.userDrafts = [];
    this.userOnlinePosts = [];
    this.allOnlinePosts = [];
    this.missions = [];
    this.activeView = 0;
    this._openCloseMission = false;
    this.openAdFilterMenu = false;
    this.toogle = false;
    this.imports = {
      DistanceSliderConfig,
      SalarySliderConfig
    };
    this.draftMenu = new PostMenu();
    this.postMenu = new PostMenu();
    this.missionMenu = new PostMenu();

    this.callBackSwipeup = (b, type) => {
      if (type == "menu") {
        this.missionMenu.swipeup = b;
      } else {
        this.missionMenu.swipeupCloseMission = b;
      }
    };
  }

  get mission() {
    console.log("mission", this.missions, this.missions[0]);
    return this.missions[0];
  }

  ngOnInit() {
    this.info.alignWith('header_search');
    combineLatest([this.profile$, this.posts$]).pipe(takeUntil(this.destroy$)).subscribe(([profile, posts]) => {
      const mapping = splitByOutput(posts, post => {
        //0 -> userOnlinePosts | 1 -> userDrafts
        if (profile.company.posts.includes(post.id)) return post.draft ? this.symbols.userDraft : this.symbols.userOnlinePost;
        return post.draft ? this.symbols.discard : this.symbols.otherOnlinePost;
      });
      const otherOnlinePost = mapping.get(this.symbols.otherOnlinePost) || [];
      this.userDrafts = mapping.get(this.symbols.userDraft) || [];
      this.userOnlinePosts = mapping.get(this.symbols.userOnlinePost) || [];
      this.allOnlinePosts = [...otherOnlinePost, ...this.userOnlinePosts];
      this.missions = this.store.selectSnapshot(DataQueries.getMany('Mission', profile.company.missions));
    });
    console.log("ngOnInit", this.missions);
    this.cd.markForCheck();
  }

  ngOnDestroy() {
    this.info.alignWith('last');
    super.ngOnDestroy();
  }

  ngAfterViewInit() {
    this.filters.filter('ST', this.allOnlinePosts);
  }

  ngOnChanges() {
    console.log("change home component");
  }

  get openCloseMission() {
    return this._openCloseMission;
  }

  set openCloseMission(b) {
    console.log("openCloseMission", b);
    this._openCloseMission = !this._openCloseMission;
  }

  swipeupMenu() {
    this.missionMenu.swipeup = !this.missionMenu.swipeup; // toogle activate update

    this.toogle = !this.toogle;
  } //factor two menu into objects


  openDraft(post) {
    this.draftMenu = assignCopy(this.draftMenu, {
      post,
      open: !!post
    });
  }

  openPost(post) {
    //mark as viewed
    this.postMenu = assignCopy(this.postMenu, {
      post,
      open: !!post,
      swipeup: false
    });
    if (post) this.store.dispatch(new MarkViewed(post.id));
  }

  setFavorite(fav) {
    this.store.dispatch(new SetFavorite(!!fav, this.postMenu.post.id));
  }

  openMission(mission) {
    this.missionMenu = assignCopy(this.missionMenu, {
      post: mission,
      open: !!mission,
      swipeup: false,
      swipeupCloseMission: false
    });
    console.log("openMission", mission);
  }

  duplicatePost(id) {
    this.info.show("info", "Duplication en cours...", Infinity);
    this.store.dispatch(new DuplicatePost(id)).pipe(take(1)).subscribe(() => {
      this.openPost(null);
      this.cd.markForCheck();
    }, () => {
      this.info.show("error", "Erreur lors de la duplication de l'annonce");
    });
  }

  pausePost(id) {
    this.store.dispatch(new SwitchPostType(id)).pipe(take(1)).subscribe(() => {
      this.openPost(null);
      this.popup.show({
        type: 'template',
