export class ProfilePageComponent {
  constructor(store, cd, info) {
    this.store = store;
    this.cd = cd;
    this.info = info;
    this.abonnementTabs = false;
    this.activeView = 0;
    this.tabView = 0;
    this.factures = new Array(10).fill(0);
    this.showResponsiveFilters = false;
  }

  setView(view) {
    this.activeView = view;
  }

  ngOnInit() {
    this.profile$.subscribe(console.log);
  }

  logout() {
    this.store.dispatch(new Logout());
  }

  actionBinding(data) {
    this.profileForm = data;
  }

  modifyProfileDesktop(binding) {
    const form = this.profileForm;
    const action = this.store.dispatch(new UserActions.ModifyUserProfile({
      profile: this.store.selectSnapshot(DataQueries.currentProfile),
      form
    }));
    this.info.show("info", "Mise à jour en cours...", Infinity);
    action.pipe(take(1)).subscribe(success => {
      // this.openModifyMenu = false;
      this.info.show("success", "Profil modifié avec succès", 2000);
      form.markAsPristine();
      form.markAsUntouched();
      this.cd.markForCheck();
    }, err => {
      this.info.show("error", "Erreur lors du modification du profil", 5000);
      this.cd.markForCheck();
    });
  }

  changePasswordDesktop(form) {
    let {
      oldPwd,
      newPwd,
      confirmPwd
    } = form.value; // console.log("this is the oasswird, oldPwd", )

    let req = this.store.dispatch(new UserActions.ChangePassword(oldPwd, newPwd));
    req.subscribe(success => {
      this.info.show("success", "Mot de passe changé avec succès", 2000);
    }, error => {
      this.info.show("error", "Erreur lors du changement du mot de passe");
    });
  }

